    public boolean actualizar(Personaje p) {
        SQLiteDatabase db = getWritableDatabase();
        boolean actualizado = true;
        db.beginTransaction();
        try {
            ContentValues valores = new ContentValues();
            valores.put("id",p.getId());
            valores.put("nombre", p.getNombre());
            valores.put("peso", p.getPeso());
            valores.put("genero", p.getGenero());
            valores.put("historia", p.getHistoria());
            valores.put("fecha", p.getFecha());
            valores.put("raza", p.getRaza());
            valores.put("partida", p.isPartida());

            // String[] args = new String[]{"usuario1", "usuario2"};
            //db.update("Personaje", valores, "nombre=? OR nombre=?", args);

            String[] args = new String[]{"id"};
            db.update("Personaje", valores, "id=?", args);

        } catch (Exception e) {
            Log.d("","");
            actualizado = false;

        } finally {
            db.endTransaction();
            db.close();
        }
        return actualizado;

    }

